import React from "react";
import { Box, Typography, IconButton, Button } from "@mui/material";
import { FavoriteBorder, Person } from "@mui/icons-material";
import { Link, useNavigate } from "react-router-dom";

const Header = () => {
  const navigate = useNavigate();

  // Admin kontrolü (string olarak "true" ise admin)
  const isAdmin = sessionStorage.getItem("isAdmin") === "true";

  return (
    <Box
      sx={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        px: 3,
        py: 2,
        borderBottom: "4px solid #6b3d3d",
        backgroundColor: "white",
      }}
    >
      {/* Logo ve başlık */}
      <Box sx={{ display: "flex", alignItems: "center", gap: 1 }}>
        <img src="logos/logo.png" alt="Logo" width="30" height="30" />
        <Typography fontWeight="bold" fontSize="18px">
          Sahibinden
        </Typography>
      </Box>

      {/* Navigasyon */}
      <Box sx={{ display: "flex", alignItems: "center", gap: 2 }}>
        <Link to="/" style={{ textDecoration: "none", color: "black" }}>
          Home
        </Link>
        <IconButton component={Link} to="/favourite">
          <FavoriteBorder />
        </IconButton>
        <IconButton component={Link} to="/profilepage">
          <Person />
        </IconButton>

        {/* ✅ Admin Page butonu */}
        {isAdmin && (
          <>
            <Button
              onClick={() => navigate("/admin")}
              sx={{
                backgroundColor: "#1976d2",
                color: "white",
                borderRadius: "8px",
                paddingX: 2,
                paddingY: 1,
                fontWeight: "bold",
                "&:hover": {
                  backgroundColor: "#115293",
                },
              }}
            >
              Admin Page
            </Button>


          </>
        )}

        <Button
          onClick={() => navigate("/")}
          sx={{
            backgroundColor: "#5c2e2e",
            color: "white",
            textTransform: "uppercase",
            borderRadius: "8px",
            boxShadow: "0 4px 10px rgba(0, 0, 0, 0.2)",
            paddingX: 3,
            paddingY: 1,
            "&:hover": {
              backgroundColor: "#4a2323",
            },
          }}
        >
          Log Out
        </Button>
      </Box>
    </Box>
  );
};

export default Header;
