import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import {
  Box,
  Typography,
  Card,
  CardContent,
  Divider,
  Grid,
  IconButton,
} from "@mui/material";
import FavoriteIcon from "@mui/icons-material/Favorite";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";

const CarPage = () => {
  const { id } = useParams(); // URL'den araba ID'si alınıyor
  const user = JSON.parse(sessionStorage.getItem("user"));
  const userId = user?.id;

  const [car, setCar] = useState(null);
  const [isFavorite, setIsFavorite] = useState(false);

  // Araba detayını getir
  useEffect(() => {
    if (!id) return;

    axios
      .get(`http://localhost:3030/api/car/${id}`)
      .then((res) => setCar(res.data))
      .catch((err) => console.error("Araç verisi alınamadı", err));
  }, [id]);

  // Favori kontrolü
  useEffect(() => {
    if (!userId || !id) return;

    axios
      .get(`http://localhost:3030/api/favorites`, {
        params: { userId },
      })
      .then((res) => {
        const favoriListesi = res.data || [];
        const eslesme = favoriListesi.find(
          (fav) => fav.car.id.toString() === id.toString()
        );
        if (eslesme) setIsFavorite(true);
      })
      .catch((err) => console.error("Favoriler alınamadı", err));
  }, [userId, id]);

  // Favoriye ekle / çıkar
  const handleFavorite = () => {
    if (!userId || !id) return;

    if (isFavorite) {
      // Favoriden çıkar
      axios
        .delete("http://localhost:3030/api/favorites", {
          params: { userId, carId: id },
        })
        .then(() => setIsFavorite(false))
        .catch((err) => {
          console.error("Favoriden çıkarma hatası", err);
        });
    } else {
      // Favoriye ekle
      axios
        .post("http://localhost:3030/api/favorites", null, {
          params: { userId, carId: id },
        })
        .then(() => setIsFavorite(true))
        .catch((err) => {
          if (err.response && err.response.status === 409) {
            alert("Bu araç zaten favorilere eklenmiş.");
            setIsFavorite(true);
          } else {
            console.error("Favori ekleme hatası", err);
          }
        });
    }
  };

  if (!car) return <Typography>Yükleniyor...</Typography>;

  return (
    <Box display="flex" justifyContent="center" mt={4} px={2}>
      <Card sx={{ width: "100%", maxWidth: 1000, borderRadius: 4, boxShadow: 6 }}>
        {/* Araç görseli */}
        {car.imagesBase64 && car.imagesBase64.length > 0 ? (
          <Box
            component="img"
            src={`data:image/jpeg;base64,${car.imagesBase64[0]}`}
            alt={`${car.brand} ${car.model}`}
            sx={{ width: "100%", height: 400, objectFit: "cover" }}
          />
        ) : (
          <Box
            sx={{
              height: 400,
              backgroundColor: "#ccc",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              fontSize: 24,
              fontWeight: "bold",
            }}
          >
            Görsel Yok
          </Box>
        )}

        {/* Bilgiler */}
        <CardContent>
          <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
            <Typography variant="h4" fontWeight="bold">
              {car.brand} {car.model}
            </Typography>
            <IconButton
              color="error"
              onClick={handleFavorite}
              size="large"
              title={isFavorite ? "Favorilerden Çıkar" : "Favorilere Ekle"}
            >
              {isFavorite ? <FavoriteIcon /> : <FavoriteBorderIcon />}
            </IconButton>
          </Box>

          <Divider sx={{ my: 2 }} />

          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <Typography><strong>Yakıt Türü:</strong> {car.fuel}</Typography>
              <Typography><strong>Model Yılı:</strong> {car.year}</Typography>
              <Typography><strong>Kilometre:</strong> {car.km} km</Typography>
            </Grid>
            <Grid item xs={12} sm={6}>
              <Typography><strong>Fiyat:</strong> {car.price} ₺</Typography>
              <Typography><strong>İletişim:</strong> {car.contact}</Typography>
            </Grid>
          </Grid>
        </CardContent>
      </Card>
    </Box>
  );
};

export default CarPage;
