import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import {
  Box,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
} from "@mui/material";

const CarListPage = () => {
  const [cars, setCars] = useState([]);
  const [selectedCars, setSelectedCars] = useState([]);
  const [showCompareBar, setShowCompareBar] = useState(false);
  const navigate = useNavigate();

  const user = JSON.parse(sessionStorage.getItem("user"));
  const userId = user?.id;

  const isAdmin = sessionStorage.getItem("isAdmin") === "true";
  let adminId = null;

  try {
    const admin = JSON.parse(sessionStorage.getItem("admin"));
    adminId = admin?.id;
    if (isAdmin && !adminId) {
      console.warn("isAdmin is true, but admin ID is missing.");
    }
  } catch (err) {
    console.warn("Admin data not found or not in correct format.");
  }

  useEffect(() => {
    axios
      .get("http://localhost:3030/api/car/main")
      .then((res) => setCars(res.data))
      .catch((err) => console.error("Failed to fetch cars", err));
  }, []);

  const handleViewDetails = (id) => {
    navigate(`/carpage/${id}`);
  };

  const handleCompareClick = (carId) => {
    if (!userId) {
      alert("Please log in first.");
      return;
    }

    let updated = [...selectedCars];
    if (updated.includes(carId)) return;

    updated.push(carId);

    if (updated.length === 1) {
      setSelectedCars(updated);
      setShowCompareBar(true);
    } else if (updated.length === 2) {
      const [car1, car2] = updated;
      axios
        .post(`http://localhost:3030/api/comparison/${userId}/${car1}/${car2}`)
        .then(() => {
          navigate("/compare");
        })
        .catch((err) => {
          console.error("Comparison error", err);
          alert("Comparison failed.");
        });
    } else {
      updated = [updated[1]];
      setSelectedCars(updated);
    }
  };

  const handleDeleteCar = (carId) => {
    if (!isAdmin || !adminId) {
      console.warn("Admin not logged in or admin ID missing.");
      alert("You must be logged in as an admin to delete a car.");
      return;
    }

    if (!window.confirm("Are you sure you want to delete this car?")) return;

    axios
      .delete(`http://localhost:3030/api/car/${adminId}/${carId}`)
      .then(() => {
        setCars((prev) => prev.filter((car) => car.id !== carId));
        alert("Car deleted successfully.");
      })
      .catch((err) => {
        console.error("Delete failed", err);
        alert("Deletion failed.");
      });
  };

  return (
    <Box p={4}>
      <Typography variant="h4" gutterBottom fontWeight="bold">
        Car Listing
      </Typography>

      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Grid container spacing={3} justifyContent="center">
            {cars.map((car) => (
              <Grid item xs={12} sm={6} md={4} key={car.id}>
                <Card>
                  {car.images && car.images.length > 0 && (
                    <CardMedia
                      component="img"
                      image={`data:image/jpeg;base64,${car.images[0].imageBase64}`}
                      alt={`${car.brand} ${car.model}`}
                      sx={{
                        height: 200,
                        objectFit: "cover",
                        aspectRatio: "16/9",
                      }}
                    />
                  )}
                  <CardContent>
                    <Typography variant="h6">
                      {car.brand} {car.model}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      {car.fuel} • {car.year} • {car.km} km
                    </Typography>
                    <Typography variant="body1" sx={{ mt: 1 }}>
                      {car.price} ₺
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Contact: {car.contact}
                    </Typography>
                  </CardContent>

                  <Button
                    onClick={() => handleViewDetails(car.id)}
                    variant="contained"
                    fullWidth
                    sx={{ backgroundColor: "#5c2e2e", borderRadius: 0 }}
                  >
                    View Details
                  </Button>

                  <Button
                    onClick={() => handleCompareClick(car.id)}
                    variant="outlined"
                    fullWidth
                    sx={{ mt: 1, borderColor: "#5c2e2e", color: "#5c2e2e" }}
                  >
                    Compare
                  </Button>

                  {isAdmin && (
                    <Button
                      onClick={() => handleDeleteCar(car.id)}
                      variant="contained"
                      color="error"
                      fullWidth
                      sx={{ mt: 1, borderRadius: 0 }}
                    >
                      Delete Car
                    </Button>
                  )}
                </Card>
              </Grid>
            ))}
          </Grid>
        </Grid>
      </Grid>

      {showCompareBar && selectedCars.length === 1 && (
        <Box
          sx={{
            position: "fixed",
            bottom: 0,
            left: 0,
            right: 0,
            bgcolor: "#eee",
            p: 2,
            boxShadow: 3,
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            zIndex: 1000,
          }}
        >
          <Typography>
            1st car selected. Please select a second car.
          </Typography>
        </Box>
      )}
    </Box>
  );
};

export default CarListPage;
