import React, { useEffect, useState } from "react";
import axios from "axios";
import { Box, Typography, Button } from "@mui/material";

const CarPage = () => {
  const [car, setCar] = useState(null);
  const carId = sessionStorage.getItem("selectedCarId");
  const userId = sessionStorage.getItem("userId"); // Kullanıcı ID'yi session'dan al

  useEffect(() => {
    if (!carId) return;
    axios
      .get(`http://localhost:3030/api/car/${carId}`)
      .then((res) => setCar(res.data))
      .catch((err) => console.error("Car fetch error", err));
  }, [carId]);

  const handleFavorite = () => {
    axios
      .post("http://localhost:3030/api/favorites", {
        userId: userId,
        carId: carId,
      })
      .then(() => alert("Favorilere eklendi!"))
      .catch((err) => console.error("Favori ekleme hatası", err));
  };

  if (!car) return <Typography>Loading...</Typography>;

  return (
    <Box p={4}>
      <Typography variant="h4" fontWeight="bold" gutterBottom>
        {car.brand} {car.model}
      </Typography>
      {car.images?.[0] && (
        <img
          src={`data:image/jpeg;base64,${car.images[0].imageBase64}`}
          alt={`${car.brand} ${car.model}`}
          style={{
            width: "100%",
            maxWidth: 900,
            borderRadius: "12px",
            objectFit: "cover",
            aspectRatio: "16/9",
          }}
        />
      )}
      <Typography variant="body1" mt={2}>
        Fuel: {car.fuel}
      </Typography>
      <Typography variant="body1">Year: {car.year}</Typography>
      <Typography variant="body1">Kilometers: {car.km} km</Typography>
      <Typography variant="body1">Price: {car.price} ₺</Typography>
      <Typography variant="body1">Contact: {car.contact}</Typography>

      <Button
        variant="contained"
        color="primary"
        onClick={handleFavorite}
        sx={{ mt: 3 }}
      >
        Favorilere Ekle
      </Button>
    </Box>
  );
};

export default CarPage;
