import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Box,
  Typography,
  List,
  ListItem,
  ListItemText,
  Button,
  Divider,
} from "@mui/material";
import { useNavigate } from "react-router-dom";

const AdminPage = () => {
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();
  const [user, setUser] = useState(null); // user state

  // Sayfa yüklendiğinde session'dan kullanıcıyı al
  useEffect(() => {
     user = JSON.parse(sessionStorage.getItem("user"));
    console.log("Session'dan gelen kullanıcı:", user);
    setUser(user);
  }, []);


  const isAdmin = sessionStorage.getItem("isAdmin") === "true";
  const adminId = user.id;


  const isLoggedIn =   isAdmin;

  useEffect(() => {
    if (!isLoggedIn) return;

    axios
      .get(`http://localhost:3030/api/user/getall/${adminId}`)
      .then((res) => {
        const incoming = res.data;
        if (Array.isArray(incoming)) {
          setUsers(incoming);
        } else {
          setUsers([]);
        }
      })
      .catch((err) => {
        console.error("Kullanıcılar alınamadı", err);
        alert("Kullanıcılar yüklenemedi.");
      });
  }, [adminId, isLoggedIn]);

  const handleDeleteUser = (userToDeleteId) => {
    if (!window.confirm("Bu kullanıcıyı silmek istediğinize emin misiniz?"))
      return;

    axios
      .delete(`http://localhost:3030/api/user/${adminId}/${userToDeleteId}`)
      .then(() => {
        setUsers((prev) => prev.filter((user) => user.id !== userToDeleteId));
        alert("Kullanıcı başarıyla silindi.");
      })
      .catch((err) => {
        console.error("Silme başarısız", err);
        alert("Kullanıcı silinemedi.");
      });
  };

  if (!isLoggedIn) {
    return (
      <Box
        height="100vh"
        display="flex"
        alignItems="center"
        justifyContent="center"
      >
        <Typography variant="h3" fontWeight="bold" color="error">
          You are not logged in
        </Typography>
      </Box>
    );
  }

  return (
    <Box p={4}>
      <Typography variant="h4" gutterBottom fontWeight="bold">
        Admin Panel – Tüm Kullanıcılar
      </Typography>

      {users.length === 0 ? (
        <Typography mt={4}>Gösterilecek kullanıcı bulunamadı.</Typography>
      ) : (
        <List>
          {users.map((user) => (
            <Box key={user.id}>
              <ListItem
                secondaryAction={
                  <Button
                    variant="contained"
                    color="error"
                    onClick={() => handleDeleteUser(user.id)}
                  >
                    Delete
                  </Button>
                }
              >
                <ListItemText
                  primary={user.username || "İsimsiz"}
                  secondary={
                    <>
                      {user.email} <br />
                      ID: {user.id}
                    </>
                  }
                />
              </ListItem>
              <Divider />
            </Box>
          ))}
        </List>
      )}
    </Box>
  );
};

export default AdminPage;
