import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Box, Typography } from "@mui/material";


const CarPage = () => {
  const { id } = useParams();
  const [car, setCar] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:8080/api/cars/${id}`)
      .then((res) => setCar(res.data))
      .catch((err) => console.error("Car fetch error", err));
  }, [id]);

  if (!car) return <Typography>Loading...</Typography>;

  return (
    <Box p={4}>
      <Typography variant="h4">{car.name}</Typography>
      <img src={car.imageUrl} alt={car.name} style={{ width: "100%", maxWidth: 600 }} />
      <Typography variant="body1">Brand: {car.brand}</Typography>
      <Typography variant="body1">Engine: {car.engine}</Typography>
      <Typography variant="body1">Year: {car.year}</Typography>
      <Typography variant="body1">KM: {car.kilometers}</Typography>
      <Typography variant="body1">Fuel Type: {car.fuelType}</Typography>
      <Typography variant="body1">Color: {car.color}</Typography>
      <Typography variant="body1" sx={{ mt: 2 }}>
        {car.description}
      </Typography>
    </Box>
  );
};

export default CarPage;
