import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import {
  Box,
  Typography,
  Card,
  CardContent,
  Divider,
  Grid,
  IconButton,
} from "@mui/material";
import FavoriteIcon from "@mui/icons-material/Favorite";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";

const CarPage = () => {
  const { id } = useParams(); // Araba ID
  const user = JSON.parse(sessionStorage.getItem("user"));

  const userId = user?.id;

  const [car, setCar] = useState(null);
  const [isFavorite, setIsFavorite] = useState(false);

  // Araba detayını getir
  useEffect(() => {
    console.log(user);
    if (!id) return;

    axios
      .get(`http://localhost:3030/api/car/${id}`)
      .then((res) => setCar(res.data))
      .catch((err) => console.error("Araç verisi alınamadı", err));
  }, [id]);

  // Favoriye ekle
  const handleFavorite = () => {
  console.log("Seçilen araba ID:", id);

  if (!userId || !id) return;

  if (isFavorite) {
    // Favoriden çıkar
    axios
      .delete("http://localhost:3030/api/favorites", {
        params: { userId, carId: id },
      })
      .then(() => {
        setIsFavorite(false); // Kalbi boş yap
      })
      .catch((err) => {
        console.error("Favoriden çıkarma hatası", err);
      });
  } else {
    // Favoriye ekle
    axios
      .post("http://localhost:3030/api/favorites", null, {
        params: { userId, carId: id },
      })
      .then(() => {
        setIsFavorite(true); // Kalbi kırmızı yap
      })
      .catch((err) => {
        if (err.response && err.response.status === 409) {
          alert("Bu araç zaten favorilere eklenmiş.");
          setIsFavorite(true); // Yine de kırmızı yap
        } else {
          console.error("Favori ekleme hatası", err);
        }
      });
  }
};


  if (!car) return <Typography>Yükleniyor...</Typography>;

  return (
    <Box display="flex" justifyContent="center" mt={4} px={2}>
      <Card
        sx={{ width: "100%", maxWidth: 1000, borderRadius: 4, boxShadow: 6 }}
      >
        {/* Araç görseli */}
        {car.images && car.images.length > 0 ? (
          <Box
            component="img"
            src={`data:image/jpeg;base64,${car.images[0].imageBase64}`}
            alt={`${car.brand} ${car.model}`}
            sx={{ width: "100%", height: 400, objectFit: "cover" }}
          />
        ) : (
          <Box
            sx={{
              height: 400,
              backgroundColor: "#ccc",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              fontSize: 24,
              fontWeight: "bold",
            }}
          >
            Görsel Yok
          </Box>
        )}

        {/* Bilgiler */}
        <CardContent>
          <Box
            display="flex"
            justifyContent="space-between"
            alignItems="center"
            mb={2}
          >
            <Typography variant="h4" fontWeight="bold">
              {car.brand} {car.model}
            </Typography>
            <IconButton
              color="error"
              onClick={handleFavorite}
              size="large"
              title="Favorilere Ekle"
            >
              {isFavorite ? <FavoriteIcon /> : <FavoriteBorderIcon />}
            </IconButton>
          </Box>

          <Divider sx={{ my: 2 }} />

          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <Typography>
                <strong>Yakıt Türü:</strong> {car.fuel}
              </Typography>
              <Typography>
                <strong>Model Yılı:</strong> {car.year}
              </Typography>
              <Typography>
                <strong>Kilometre:</strong> {car.km} km
              </Typography>
            </Grid>
            <Grid item xs={12} sm={6}>
              <Typography>
                <strong>Fiyat:</strong> {car.price} ₺
              </Typography>
              <Typography>
                <strong>İletişim:</strong> {car.contact}
              </Typography>
            </Grid>
          </Grid>
        </CardContent>
      </Card>
    </Box>
  );
};

export default CarPage;
