import React, { useEffect, useState } from "react";
import axios from "axios";

const FavouritePage = () => {
  const [favourites, setFavourites] = useState([]);
  const user = JSON.parse(sessionStorage.getItem("user"));
  const userId = user?.id;

  useEffect(() => {
    if (userId) {
      fetchFavourites();
    }
  }, [userId]);

  const fetchFavourites = async () => {
    try {
      const response = await axios.get("http://localhost:3030/api/favorites", {
        params: { userId },
      });

      const favoriteCars = await Promise.all(
        response.data.map(async (fav) => {
          const res = await axios.get(
            `http://localhost:3030/api/car/${fav.car.id}`
          );
          return res.data;
        })
      );

      setFavourites(favoriteCars);
    } catch (error) {
      console.error("Favori araçlar alınamadı:", error);
    }
  };

  const handleRemoveFavourite = async (carId) => {
    try {
      await axios.delete("http://localhost:3030/api/favorites", {
        params: { userId, carId },
      });
      setFavourites((prev) => prev.filter((car) => car.id !== carId));
    } catch (error) {
      console.error("Favori silme hatası:", error);
    }
  };

  return (
    <div style={{ padding: "40px" }}>
      <h2>Favori Araçlarım</h2>
      {favourites.length === 0 ? (
        <p>Henüz favori araç yok.</p>
      ) : (
        <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
          {favourites.map((car) => (
            <div
              key={car.id}
              style={{
                border: "1px solid #ccc",
                padding: "16px",
                width: "240px",
                borderRadius: "8px",
              }}
            >
              {/* Güvenli görsel gösterimi */}
              {car.images && car.images.length > 0 && car.images[0]?.imageBase64 ? (
                <img
                  src={`data:image/jpeg;base64,${car.images[0].imageBase64}`}
                  alt={`${car.brand} ${car.model}`}
                  style={{ width: "100%", height: "150px", objectFit: "cover" }}
                />
              ) : (
                <div
                  style={{
                    width: "100%",
                    height: "150px",
                    backgroundColor: "#ddd",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    color: "#555",
                    fontSize: "14px",
                    fontWeight: "bold",
                  }}
                >
                  Görsel Yok
                </div>
              )}
              <h4>{car.brand} {car.model}</h4>
              <p><strong>Fiyat:</strong> {car.price} ₺</p>
              <button
                onClick={() => handleRemoveFavourite(car.id)}
                style={{
                  backgroundColor: "#b30000",
                  color: "white",
                  border: "none",
                  padding: "8px 12px",
                  cursor: "pointer",
                  borderRadius: "4px",
                }}
              >
                Kaldır
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default FavouritePage;
