import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Box,
  Typography,
  Grid,
  CircularProgress,
  Paper,
  Divider,
} from "@mui/material";
import CheckIcon from "@mui/icons-material/Check";

const ComparePage = () => {
  const [car1, setCar1] = useState(null);
  const [car2, setCar2] = useState(null);
  const [loading, setLoading] = useState(true);
  const [analysis, setAnalysis] = useState("");

  const user = JSON.parse(sessionStorage.getItem("user"));
  const userId = user?.id;

  useEffect(() => {
    if (!userId) return;
    axios
      .get(`http://localhost:3030/api/comparison/${userId}`)
      .then((res) => {
        const dto = res.data;
        setCar1(dto.car2); // displayed left
        setCar2(dto.car1); // displayed right
      })
      .catch((err) => {
        console.error("Failed to fetch comparison data:", err);
      })
      .finally(() => setLoading(false));
  }, [userId]);

  useEffect(() => {
    if (car1 && car2) {
      const a = [];

      if (car1.km !== car2.km) {
        const better = car1.km < car2.km ? car1 : car2;
        a.push(`${better.brand} ${better.model} has lower mileage.`);
      }

      if (car1.year !== car2.year) {
        const newer = car1.year > car2.year ? car1 : car2;
        a.push(`${newer.brand} ${newer.model} is a newer model.`);
      }

      if (car1.price !== car2.price) {
        const cheaper = car1.price < car2.price ? car1 : car2;
        a.push(`${cheaper.brand} ${cheaper.model} is more affordable.`);
      }

      if (a.length === 0) {
        a.push("Both vehicles are very similar.");
      }

      setAnalysis(a.join(" "));
    }
  }, [car1, car2]);

  const isNumeric = (val) => !isNaN(parseFloat(val)) && isFinite(val);

  const renderFeatureRow = (label, key) => {
    const val1 = car1?.[key];
    const val2 = car2?.[key];
    const isEqual = val1 === val2;

    const compare = () => {
      if (!isNumeric(val1) || !isNumeric(val2)) return [false, false];
      const num1 = parseFloat(val1);
      const num2 = parseFloat(val2);
      if (key === "price" || key === "km") {
        return [num1 < num2, num2 < num1];
      } else {
        return [num1 > num2, num2 > num1];
      }
    };

    const [better1, better2] = compare();

    const style = (highlight) => ({
      fontWeight: highlight ? "bold" : "normal",
      color: highlight ? "green" : "inherit",
      textAlign: "center",
    });

    return (
      <Grid container spacing={2} alignItems="center" key={key} sx={{ py: 1 }}>
        <Grid item xs={4}>
          <Typography sx={style(better1)}>{val1}</Typography>
        </Grid>
        <Grid item xs={4} textAlign="center">
          <Typography color="text.secondary">{label}</Typography>
          {isEqual && <CheckIcon sx={{ color: "green", ml: 1 }} />}
        </Grid>
        <Grid item xs={4}>
          <Typography sx={style(better2)}>{val2}</Typography>
        </Grid>
      </Grid>
    );
  };

  if (loading) return <CircularProgress sx={{ m: 5 }} />;
  if (!car1 || !car2)
    return <Typography m={5}>Comparison data not available.</Typography>;

  return (
    <Box p={4}>
      <Typography variant="h4" gutterBottom textAlign="center">
        ðŸš— Vehicle Comparison
      </Typography>

      {/* Resim KutularÄ± (Fixed YÃ¼kseklik ve Hizalama) */}
      <Grid container spacing={2} justifyContent="center" alignItems="center" sx={{ mb: 4 }}>
        {[car1, car2].map((car, index) => (
          <Grid item xs={12} md={5} key={index}>
            <Paper elevation={3}>
              {car.images?.[0] && (
                <Box
                  sx={{
                    height: 300,
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    overflow: "hidden",
                    backgroundColor: "#f4f4f4",
                  }}
                >
                  <Box
                    component="img"
                    src={`data:image/jpeg;base64,${car.images[0].imageBase64}`}
                    alt={`${car.brand} ${car.model}`}
                    sx={{
                      maxHeight: "100%",
                      maxWidth: "100%",
                      objectFit: "contain",
                    }}
                  />
                </Box>
              )}
              <Box p={2} textAlign="center">
                <Typography variant="h6">
                  {car.brand} {car.model}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {car.fuel}
                </Typography>
              </Box>
            </Paper>
          </Grid>
        ))}
      </Grid>

      {/* Ã–zellikler */}
      <Paper elevation={2} sx={{ p: 3, mb: 4 }}>
        {renderFeatureRow("Price (â‚º)", "price")}
        <Divider sx={{ my: 1 }} />
        {renderFeatureRow("Kilometers", "km")}
        <Divider sx={{ my: 1 }} />
        {renderFeatureRow("Model Year", "year")}
        <Divider sx={{ my: 1 }} />
        {renderFeatureRow("Fuel Type", "fuel")}
        <Divider sx={{ my: 1 }} />
        {renderFeatureRow("Contact", "contact")}
      </Paper>

      {/* AkÄ±llÄ± Yorum */}
      <Paper elevation={2} sx={{ p: 3 }}>
        <Typography variant="h6" gutterBottom>
          ðŸ§  Smart Analysis
        </Typography>
        <Typography>{analysis}</Typography>
      </Paper>
    </Box>
  );
};

export default ComparePage;
