import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Box,
  Typography,
  List,
  ListItem,
  ListItemText,
  Button,
  Divider,
} from "@mui/material";
import { useNavigate } from "react-router-dom";

const AdminPage = () => {
  const [users, setUsers] = useState([]);
  const navigate = useNavigate();

  const isAdmin = sessionStorage.getItem("isAdmin") === "true";
  const admin = JSON.parse(sessionStorage.getItem("admin"));
  const adminId = admin?.id;

  useEffect(() => {
    if (!isAdmin || !adminId) {
      alert("Yetkisiz erişim. Ana sayfaya yönlendiriliyorsunuz.");
      navigate("/");
      return;
    }

    axios
      .get(`http://localhost:3030/api/user/getall/${adminId}`)
      .then((res) => {
        setUsers(res.data);
      })
      .catch((err) => {
        console.error("Kullanıcılar alınamadı", err);
        alert("Kullanıcılar yüklenemedi.");
      });
  }, [adminId, isAdmin, navigate]);

  const handleDeleteUser = (userToDeleteId) => {
    if (!window.confirm("Bu kullanıcıyı silmek istediğinize emin misiniz?")) return;

    axios
      .delete(`http://localhost:3030/api/user/${adminId}/${userToDeleteId}`)
      .then(() => {
        setUsers((prev) => prev.filter((user) => user.id !== userToDeleteId));
        alert("Kullanıcı başarıyla silindi.");
      })
      .catch((err) => {
        console.error("Silme başarısız", err);
        alert("Kullanıcı silinemedi.");
      });
  };

  return (
    <Box p={4}>
      <Typography variant="h4" gutterBottom fontWeight="bold">
        Admin Panel – Tüm Kullanıcılar
      </Typography>

      <List>
        {users.map((user) => (
          <Box key={user.id}>
            <ListItem
              secondaryAction={
                <Button
                  variant="contained"
                  color="error"
                  onClick={() => handleDeleteUser(user.id)}
                >
                  Delete
                </Button>
              }
            >
              <ListItemText
                primary={`${user.name} (${user.email})`}
                secondary={`ID: ${user.id}`}
              />
            </ListItem>
            <Divider />
          </Box>
        ))}
      </List>
    </Box>
  );
};

export default AdminPage;
