import React from "react";
import {
  Box,
  Typography,
  IconButton,
  Button
} from "@mui/material";
import {
  FavoriteBorder,
  Person,
  DirectionsCar
} from "@mui/icons-material";
import { Link, useNavigate } from "react-router-dom";

const Header = () => {
  const navigate = useNavigate();

  const isAdmin = sessionStorage.getItem("isAdmin") === "true";
  const user = JSON.parse(sessionStorage.getItem("user"));

  const handleLogout = () => {
    sessionStorage.clear();
    navigate("/login");
  };

  const handleAddCar = () => {
    if (isAdmin) {
      alert("Admin kullanıcılar araç ekleyemez.");
      return;
    }
    navigate("/add-car");
  };

  return (
    <Box
      sx={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        px: 3,
        py: 2,
        borderBottom: "4px solid #6b3d3d",
        backgroundColor: "white",
      }}
    >
      {/* Logo */}
<Box sx={{ display: "flex", alignItems: "center", gap: 1 }}>
  <Box
    component="img"
    src="logos/logo.png"
    alt="Logo"
    sx={{
      width: 40,
      height: 40,
      borderRadius: "50%",
      objectFit: "cover",
      border: "2px solid #6b3d3d",
      backgroundColor: "#fff",
    }}
  />
  <Typography fontWeight="bold" fontSize="18px">
    Sahibinden
  </Typography>
</Box>


      {/* Navigasyon */}
      <Box sx={{ display: "flex", alignItems: "center", gap: 2 }}>
        <Link to="/" style={{ textDecoration: "none", color: "black" }}>
          Home
        </Link>

        <IconButton component={Link} to="/favourite">
          <FavoriteBorder />
        </IconButton>

        <IconButton component={Link} to="/profilepage">
          <Person />
        </IconButton>

        {/* Sadece normal kullanıcılar için "Add Car" ve "My Cars" */}
        {!isAdmin && (
          <>
            <Button
              onClick={handleAddCar}
              sx={{
                backgroundColor: "#2e7d32",
                color: "white",
                fontWeight: "bold",
                borderRadius: "8px",
                paddingX: 2,
                paddingY: 1,
                "&:hover": {
                  backgroundColor: "#1b5e20",
                },
              }}
            >
              Add Car
            </Button>

            <Button
              onClick={() => navigate("/my-car-listing")}
              startIcon={<DirectionsCar />}
              sx={{
                backgroundColor: "#6a1b9a",
                color: "white",
                fontWeight: "bold",
                borderRadius: "8px",
                paddingX: 2,
                paddingY: 1,
                "&:hover": {
                  backgroundColor: "#4a148c",
                },
              }}
            >
              My Cars
            </Button>
          </>
        )}

        {/* Admin Panel Butonu */}
        {isAdmin && (
          <Button
            onClick={() => navigate("/admin")}
            sx={{
              backgroundColor: "#1976d2",
              color: "white",
              borderRadius: "8px",
              paddingX: 2,
              paddingY: 1,
              fontWeight: "bold",
              "&:hover": {
                backgroundColor: "#115293",
              },
            }}
          >
            Admin Page
          </Button>
        )}

        {/* Log Out */}
        <Button
          onClick={handleLogout}
          sx={{
            backgroundColor: "#5c2e2e",
            color: "white",
            textTransform: "uppercase",
            borderRadius: "8px",
            boxShadow: "0 4px 10px rgba(0, 0, 0, 0.2)",
            paddingX: 3,
            paddingY: 1,
            "&:hover": {
              backgroundColor: "#4a2323",
            },
          }}
        >
          Log Out
        </Button>
      </Box>
    </Box>
  );
};

export default Header;
