import React, { useEffect, useState } from "react";
import axios from "axios";

const FavouritePage = () => {
  const [favourites, setFavourites] = useState([]);
  const userId = localStorage.getItem("userId"); // Girişte kaydedilmişse

  useEffect(() => {
    fetchFavourites();
  }, []);

  const fetchFavourites = async () => {
    try {
      const response = await axios.get(
        `http://localhost:8080/api/favourites/user/${userId}`
      );
      setFavourites(response.data);
    } catch (error) {
      console.error("Error fetching favourites:", error);
    }
  };

  const handleRemoveFavourite = async (productId) => {
    try {
      await axios.delete(
        `http://localhost:8080/api/favourites/user/${userId}/product/${productId}`
      );
      setFavourites((prev) => prev.filter((item) => item.id !== productId));
    } catch (error) {
      console.error("Error removing favourite:", error);
    }
  };

  return (
    <div style={{ padding: "40px" }}>
      <h2>My Favourite Products</h2>
      {favourites.length === 0 ? (
        <p>No favourite products yet.</p>
      ) : (
        <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
          {favourites.map((product) => (
            <div
              key={product.id}
              style={{
                border: "1px solid #ccc",
                padding: "16px",
                width: "240px",
                borderRadius: "8px",
              }}
            >
              <img
                src={product.imageUrl}
                alt={product.name}
                style={{ width: "100%", height: "150px", objectFit: "cover" }}
              />
              <h4>{product.name}</h4>
              <button
                onClick={() => handleRemoveFavourite(product.id)}
                style={{
                  backgroundColor: "#b30000",
                  color: "white",
                  border: "none",
                  padding: "8px 12px",
                  cursor: "pointer",
                  borderRadius: "4px",
                }}
              >
                Remove
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default FavouritePage;
