import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Box,
  Typography,
  Button,
  Card,
  CardContent,
  CardMedia,
  Divider,
  Grid,
} from "@mui/material";
import FavoriteIcon from "@mui/icons-material/Favorite";

const CarPage = () => {
  const [car, setCar] = useState(null);
  const carId = sessionStorage.getItem("selectedCarId");
  const userId = sessionStorage.getItem("userId");

  useEffect(() => {
    if (!carId) return;
    axios
      .get(`http://localhost:3030/api/car/${carId}`)
      .then((res) => setCar(res.data))
      .catch((err) => console.error("Car fetch error", err));
  }, [carId]);

  const handleFavorite = () => {
    axios
      .post("http://localhost:3030/api/favorites", null, {
        params: {
          userId: userId,
          carId: carId,
        },
      })
      .then(() => alert("Favorilere eklendi!"))
      .catch((err) => console.error("Favori ekleme hatası", err));
  };

  if (!car) return <Typography>Loading...</Typography>;

  return (
    <Box display="flex" justifyContent="center" mt={4} px={2}>
      <Card
        sx={{
          width: "100%",
          maxWidth: 1000,
          borderRadius: 4,
          boxShadow: 6,
          overflow: "hidden",
        }}
      >
        {car.images?.[0] && (
          <CardMedia
            component="img"
            height="400"
            image={`data:image/jpeg;base64,${car.images[0].imageBase64}`}
            alt={`${car.brand} ${car.model}`}
            sx={{ objectFit: "cover" }}
          />
        )}
        <CardContent>
          <Box
            display="flex"
            justifyContent="space-between"
            alignItems="center"
          >
            <Typography variant="h4" fontWeight="bold">
              {car.brand} {car.model}
            </Typography>
            <Button
              variant="contained"
              color="secondary"
              onClick={handleFavorite}
              startIcon={<FavoriteIcon />}
            >
              Favorilere Ekle
            </Button>
          </Box>

          <Divider sx={{ my: 2 }} />

          <Grid container spacing={2}>
            <Grid item xs={6}>
              <Typography variant="body1">
                <strong>Yakıt Türü:</strong> {car.fuel}
              </Typography>
              <Typography variant="body1">
                <strong>Model Yılı:</strong> {car.year}
              </Typography>
              <Typography variant="body1">
                <strong>Kilometre:</strong> {car.km} km
              </Typography>
            </Grid>
            <Grid item xs={6}>
              <Typography variant="body1">
                <strong>Fiyat:</strong> {car.price} ₺
              </Typography>
              <Typography variant="body1">
                <strong>İletişim:</strong> {car.contact}
              </Typography>
            </Grid>
          </Grid>
        </CardContent>
      </Card>
    </Box>
  );
};

export default CarPage;
