import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import {
  Box,
  Typography,
  Card,
  CardContent,
  Divider,
  Grid,
  IconButton,
} from "@mui/material";
import FavoriteIcon from "@mui/icons-material/Favorite";

const CarPage = () => {
  const { id } = useParams(); // ðŸ‘ˆ URLâ€™den id al
  const userId = sessionStorage.getItem("userId");
  const [car, setCar] = useState(null);

  useEffect(() => {
    if (!id) return;

    axios
      .get(`http://localhost:3030/api/car/${id}`)
      .then((res) => setCar(res.data))
      .catch((err) => console.error("AraÃ§ verisi alÄ±namadÄ±", err));
  }, [id]);

  const handleFavorite = () => {
    axios
      .post("http://localhost:3030/api/favorites", null, {
        params: { userId, carId: id },
      })
      .then(() => alert("Favorilere eklendi!"))
      .catch((err) => console.error("Favori ekleme hatasÄ±", err));
  };

  if (!car) return <Typography>YÃ¼kleniyor...</Typography>;

  return (
    <Box display="flex" justifyContent="center" mt={4} px={2}>
      <Card sx={{ width: "100%", maxWidth: 1000, borderRadius: 4, boxShadow: 6 }}>
        {/* GÃ¶rsel */}
        {car.images && car.images.length > 0 ? (
          <Box
            component="img"
            src={`data:image/jpeg;base64,${car.images[0].imageBase64}`}
            alt={`${car.brand} ${car.model}`}
            sx={{ width: "100%", height: 400, objectFit: "cover" }}
          />
        ) : (
          <Box
            sx={{
              height: 400,
              backgroundColor: "#ccc",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              fontSize: 24,
              fontWeight: "bold",
            }}
          >
            GÃ¶rsel Yok
          </Box>
        )}

        {/* Bilgiler */}
        <CardContent>
          <Box display="flex" justifyContent="space-between" alignItems="center" mb={2}>
            <Typography variant="h4" fontWeight="bold">
              {car.brand} {car.model}
            </Typography>
            <IconButton
              color="error"
              onClick={handleFavorite}
              size="large"
              title="Favorilere Ekle"
            >
              <FavoriteIcon />
            </IconButton>
          </Box>

          <Divider sx={{ my: 2 }} />

          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <Typography><strong>YakÄ±t TÃ¼rÃ¼:</strong> {car.fuel}</Typography>
              <Typography><strong>Model YÄ±lÄ±:</strong> {car.year}</Typography>
              <Typography><strong>Kilometre:</strong> {car.km} km</Typography>
            </Grid>
            <Grid item xs={12} sm={6}>
              <Typography><strong>Fiyat:</strong> {car.price} â‚º</Typography>
              <Typography><strong>Ä°letiÅŸim:</strong> {car.contact}</Typography>
            </Grid>
          </Grid>
        </CardContent>
      </Card>
    </Box>
  );
};

export default CarPage;
