import React, { useState } from "react";
import { Box, TextField, MenuItem, Button, Typography } from "@mui/material";

const CarFilterSidebar = ({ onFilter }) => {
  const [filters, setFilters] = useState({
    brand: "",
    model: "",
    fuel: "",
    km: "",
    kmDirection: "less",
    price: "",
    priceDirection: "less",
  });

  const handleChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };

  const applyFilters = () => {
    onFilter(filters);
  };

  return (
   <Box sx={{ width: '100%' }}>
  <Typography variant="h6" gutterBottom sx={{ color: "#5c2e2e", fontWeight: "bold" }}>
    Filtrele
  </Typography>

  <TextField
    label="Marka"
    name="brand"
    value={filters.brand}
    onChange={handleChange}
    fullWidth
    margin="normal"
    variant="outlined"
  />
  <TextField
    label="Model"
    name="model"
    value={filters.model}
    onChange={handleChange}
    fullWidth
    margin="normal"
    variant="outlined"
  />
  <TextField
    label="Yakıt Tipi"
    name="fuel"
    value={filters.fuel}
    onChange={handleChange}
    fullWidth
    margin="normal"
    variant="outlined"
  />
  <TextField
    label="Kilometre"
    name="km"
    type="number"
    value={filters.km}
    onChange={handleChange}
    fullWidth
    margin="normal"
    variant="outlined"
  />
  <TextField
    select
    label="Km Yönü"
    name="kmDirection"
    value={filters.kmDirection}
    onChange={handleChange}
    fullWidth
    margin="normal"
    variant="outlined"
  >
    <MenuItem value="less">Daha az</MenuItem>
    <MenuItem value="bigger">Daha fazla</MenuItem>
  </TextField>
  <TextField
    label="Fiyat"
    name="price"
    type="number"
    value={filters.price}
    onChange={handleChange}
    fullWidth
    margin="normal"
    variant="outlined"
  />
  <TextField
    select
    label="Fiyat Yönü"
    name="priceDirection"
    value={filters.priceDirection}
    onChange={handleChange}
    fullWidth
    margin="normal"
    variant="outlined"
  >
    <MenuItem value="less">Daha az</MenuItem>
    <MenuItem value="bigger">Daha fazla</MenuItem>
  </TextField>

  <Button
    variant="contained"
    fullWidth
    onClick={applyFilters}
    sx={{
      mt: 2,
      backgroundColor: "#5c2e2e",
      color: "#fff",
      "&:hover": { backgroundColor: "#4b2424" },
    }}
  >
    Filtrele
  </Button>
</Box>

  );
};

export default CarFilterSidebar;
